mapper.select:
select count(id) from setmeal

    and status = #{status}
    and category_id = #{categoryId}

select * from setmeal


        and category_id = #{categoryId}


        and status = #{status}


        and name like concat('%',#{name},'%')

select * from setmeal

    and name like concat('%',#{name},'%')
    and category_id = #{categoryId}
    and status = #{status}
